package Nov1;

public class Forloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub1
		//print name for 5 times
	
		
		for(int i=1;i<=5;i++)
		{
			System.out.println("poonu  " +i);

		}
		System.out.println("-----------");
		for(int i=1;i<=5;i+=2)
		{
			System.out.println("poonu    "+i);
		}
		System.out.println("--------------");
		for(int i=5;i>=1;i--)
		{
			System.out.println("poonu   "+i);
		}
	}
	
	}
